import { CliUx, Command } from '@oclif/core';
import { installPackageTemplate } from '../helpers/template-installer.js';
export default class Create extends Command {
    static description = 'Creates a new Steamship Package or Plugin';
    static examples = [`$ ship create project-handle`];
    static flags = {};
    static args = [
        {
            name: 'handle',
            required: false,
            description: 'The new project handle.'
        }
    ];
    async run() {
        const { args, flags } = await this.parse(Create);
        try {
            await installPackageTemplate(process.cwd(), args.handle);
        }
        catch (e) {
            CliUx.ux.error(e);
        }
    }
}
