import { Command, Flags } from '@oclif/core';
import { Workspace } from '@steamship/client';
import { deleteObject } from '../../helpers/delete-object.js';
export default class Delete extends Command {
    static description = 'Delete a Workspace.';
    static examples = ['ship workspace:delete <handle>'];
    static flags = {
        handle: Flags.string({ char: 'h', description: 'the workspace handle' })
    };
    static args = [];
    async run() {
        const { args, flags } = await this.parse(Delete);
        await deleteObject(Workspace, { handle: flags.handle }, 'Workspace');
    }
}
