import { CliUx } from '@oclif/core';
import { getAuthedClient } from './get-client.js';
export async function deleteObject(klass, deleteParams, nameForLogging) {
    const client = await getAuthedClient();
    const request = await klass.get(client, deleteParams);
    if (!request.output) {
        CliUx.ux.error(`Could not retrieve ${nameForLogging} from Steamship.`);
    }
    const deleteRequest = await request.output.delete();
    if (!request.output) {
        CliUx.ux.error(`Could not delete ${nameForLogging}.`);
    }
    else {
        CliUx.ux.info(`${nameForLogging} deleted.`);
    }
}
