import { CliUx } from '@oclif/core';
import { table } from 'table';
export function showTable(params) {
    const size = params.headers.length;
    const firstRow = [params.title];
    for (let i = 1; i < size; i++) {
        firstRow.push('');
    }
    const headers = [firstRow, params.headers];
    const allrows = [...headers, ...params.data];
    const config = {
        spanningCells: [{ col: 0, row: 0, colSpan: size, alignment: 'center' }]
    };
    CliUx.ux.log(table(allrows, config));
}
