import { CliUx } from '@oclif/core';
export async function getHandle(params) {
    if (params.provided) {
        return params.provided;
    }
    else if (params.config && params.config.type == params.type && params.config.handle) {
        return params.config.handle;
    }
    else {
        return CliUx.ux.prompt(`Please enter the ${params.type} handle`);
    }
}
