import { select } from './select.js';
export async function getPluginType() {
    const choices = [
        {
            id: 'corpusImporter',
            name: 'Corpus Importer',
            description: 'Import a corpus from another service.'
        },
        {
            id: 'corpusExporter',
            name: 'Corpus Exporter',
            description: 'Export a corpus to another service.'
        },
        {
            id: 'fileImporter',
            name: 'File Importer',
            description: 'Import a file from another service.'
        },
        {
            id: 'blockifier',
            name: 'Blockifier',
            description: 'Convert imported data into Steamship Format.'
        },
        {
            id: 'tagger',
            name: 'Tagger',
            description: 'Tag files and blocks (classificaiton, parsing, NER, sentiment, etc).'
        },
        { id: 'embedder', name: 'Embedder', description: 'Embed text into vectors of meaning.' }
    ];
    return (await select({ prompt: 'What kind of plugin?', choices })).id;
}
