import { CliUx } from '@oclif/core';
import { Workspace } from '@steamship/client';
import { getHandle } from './get-handle.js';
export async function getWorkspace(params) {
    const handle = await getHandle({
        provided: params.providedHandle,
        config: params.config,
        type: 'workspace'
    });
    const workspace = (await Workspace.get(params.client, {
        handle: handle
    })).output;
    if (!workspace) {
        CliUx.ux.error('Unable to load workspace');
        CliUx.ux.exit(-1);
    }
    return workspace;
}
